import React from 'react';
import { Link } from 'react-router-dom';
class Cart extends React.Component {
  constructor() {
    super();
    this.increaseValue = this.increaseValue.bind(this);
    this.decreaseValue = this.decreaseValue.bind(this);
    this.removeProduct = this.removeProduct.bind(this);
    this.state = {
      cart: '',
      number: 1,
    };
  }
  componentDidMount() {
    this.loadCart();
  }
  loadCart() {
    const LOAD_CART = JSON.parse(localStorage.getItem('Cart'));
    // console.log(LOAD_CART);
    this.setState({ cart: LOAD_CART });
  }
  increaseValue() {
    // const { number } = this.state;
    // number += 1;
    this.setState((currentState) => ({ number: currentState.number + 1 }), () => {});
  }
  decreaseValue({ target }) {
    const { number } = this.state;
    console.log(target.id);
    if (number === 1) {
      this.removeProductFromDecrease(target.id);
    }
    this.setState((currentState) => ({ number: currentState.number - 1 }), () => {});
  }
  removeProductFromDecrease(id) {
    const { cart } = this.state;
    const NEW_CART = cart.filter((element) => element.id !== id);
    localStorage.setItem('Cart', JSON.stringify(NEW_CART));
    this.loadCart();
  }
  removeProduct({ target }) {
    const { cart } = this.state;
    const NEW_CART = cart.filter((element) => element.id !== target.id);
    localStorage.setItem('Cart', JSON.stringify(NEW_CART));
    this.loadCart();
  }
  render() {
    const { cart, number } = this.state;
    if (cart.length > 0) {
      return cart.map((element) => (
        <div key={ element.id }>
          <Link
            to={ { pathname: `/product-details/${element.id}`, state: { element } } }
            data-testid="product-detail-link"
          >
            <div className="card" data-testid="product">
              <h1 data-testid="shopping-cart-product-name">{ element.title }</h1>
              <img src={ element.thumbnail } alt={ element.title } />
              <h3>{`R$: ${element.price}`}</h3>
            </div>
          </Link>
          <button
            id={ element.id }
            type="button"
            data-testid="product-decrease-quantity"
            onClick={ this.decreaseValue }
          >
            -
          </button>
          <p>Quantidade: </p>
          <p data-testid="shopping-cart-product-quantity">{ number }</p>
          <button
            id={ element.id }
            type="button"
            data-testid="product-increase-quantity"
            onClick={ this.increaseValue }
          >
            +
          </button>
          <button
            id={ element.id }
            type="button"
            data-testid=""
            onClick={ this.removeProduct }
          >
            Remover Produto
          </button>
        </div>
      ));
    }
    return (
      <div>
        <p
          data-testid="shopping-cart-empty-message"
        >
          Seu carrinho está vazio
        </p>
      </div>
    );
  }
}
export default Cart;
