import React from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';

class ProductDetails extends React.Component {
  constructor(props) {
    super(props);
    this.getProductClicked = this.getProductClicked.bind(this);
    this.addCart = this.addCart.bind(this);
    this.evaluations = this.evaluations.bind(this);
    this.state = {
      readyToRender: false,
    };
  }

  addCart() {
    const { location: { state: { element } } } = this.props;
    const CART = JSON.parse(localStorage.getItem('Cart'));
    localStorage.setItem('Cart', JSON.stringify([...CART, element]));
  }

  componentDidMount() {
    this.getProductClicked();
  }

  async getProductClicked() {
    const timeout = 500;
    setTimeout(() => {
      this.setState({ readyToRender: true });
    }, timeout);
  }

  evaluations() {

  }

  render() {
    const { location: { state: { element } } } = this.props;
    const { readyToRender } = this.state;
    return (
      <div>
        <h1>Product Details Page</h1>
        {!readyToRender ? <p>Carregando...</p> : (
          <div>
            <h1 data-testid="product-detail-name">{element.title}</h1>
            <img src={ element.thumbnail } alt="title" />
            <p>{`Preço: ${element.price}`}</p>
            {element.attributes.map(({ name, value_name: valueName }, index) => (
              <p key={ index }>{`${name}: ${valueName}`}</p>
            ))}
            <p>{element.warranty}</p>
            <button
              type="button"
              data-testid="product-detail-add-to-cart"
              onClick={ this.addCart }
            >
              Adicionar ao carrinho
            </button>
            <Link to="/cart" data-testid="shopping-cart-button">
            <button
              type="button"
            >
              Carrinho
            </button>
          </Link>
          <form>
            <div className="input-email-product-details">
            <input
              type="email"
              placeholder="Digite se email"
              required
            />
            <input
              type="number"
              min="0"
              max="5"
              required
              placeholder="Nota"
            />
            </div>
            <div className="textarea-product-details">
              <textarea
                data-testid="product-detail-evaluation"
                id="feedback-evaluation-description"
                name="feedback-evaluation-description"
                placeholder="Mensagem opcional"
                maxLength="500"
                rows="7"
                cols="35"
              />
            </div>
            <div className="btn-evaluate">
              <button
                type="button"
                onClick={ this.evaluations }
              >
                Avaliar
              </button>
            </div>
          </form>
          </div>
        )}
      </div>
    );
  }
}

ProductDetails.propTypes = {
  location: PropTypes.shape({
    state: PropTypes.shape({
      element: PropTypes.shape({
        title: PropTypes.string.isRequired,
        thumbnail: PropTypes.string.isRequired,
        price: PropTypes.number.isRequired,
        attributes: PropTypes.arrayOf(PropTypes.object).isRequired,
      }).isRequired,
    }),
  }).isRequired,
};

export default ProductDetails;
