import React from 'react';
import { getProductsFromCategoryAndQuery } from '../services/api';

class ProductDetails extends React.Component {
  constructor(props) {
    super(props);
    this.getProductClicked = this.getProductClicked.bind(this);
    this.state = {
      product: {
        title: 'title',
        thumbnail: 'thumbnail',
        price: 'price',
        attributes: [{
          name: 'attribute-name',
          value_name: 'value_name',
        }],
        warranty: 'warranty',
      },
      readyToRender: false,
    };
  }

  componentDidMount() {
    this.getProductClicked();
  }

  async getProductClicked() {
    const { match: { params: { id } } } = this.props;
    const itemJSON = await getProductsFromCategoryAndQuery('', '', id);
    // console.log(itemJSON);
    // console.log(id);
    this.setState({ product: itemJSON, readyToRender: true, id });
    console.log(itemJSON);
  }

  funcTste = () => {
    const { product } = this.state;
    console.log(product);
    // if (product.length < 1) {
    //   return (
    //     <div>
    //       Carregando...
    //     </div>
    //   )
    // }
    return (
      product.attributes &&
      <div>
        <h1 data-testid="product-detail-name">{product.title}</h1>
        <img src={ product.thumbnail } alt="title" />
        <p>{product.price}</p>
        {product.attributes.map(({ name, value_name: valueName }, index) => (
          <p key={ index }>{`${name}: ${valueName}`}</p>
        ))}
        <p>{product.warranty}</p>
      </div>
    );
  }

  render() {
    // const {
    //   product,
    //   // {
    //   //   title,
    //   //   thumbnail,
    //   //   price,
    //   //   attributes,
    //   //   warranty,
    //   // },
    //   readyToRender,
    // } = this.state;
    // console.log(product);
    const { product, readyToRender } = this.state;
    return (
      <div>
        <h1>Product Details Page</h1>
        {/* {console.log(product)} */}
        {readyToRender ? (
          this.funcTste()
        ) : <p>Carregando...</p>}
      </div>
    );
  }
}

export default ProductDetails;
